import React, { useRef } from "react";

export default function Question({
  currentDisplayingQuestion,
  handleShowNextButton,
  showNextButton,
  showingNextQuestion,
}) {
  const optionRefs = useRef([]);
  const { question, options, correctOption, points } =
    currentDisplayingQuestion;
  console.log(currentDisplayingQuestion);

  function handleButtonOptionClick(idx) {
    // Loop through option refs
    optionRefs.current.forEach((optionRef, index) => {
      if (index === idx) {
        // Apply 'answer' class to clicked button
        optionRef.classList.add("answer");
      }
      if (index === correctOption) {
        // Apply 'correct' class to correct option
        optionRef.classList.add("correct");
      } else {
        // Apply 'wrong' class to incorrect options
        optionRef.classList.add("wrong");
      }
      // disable all buttons after click on any button
      optionRef.disabled = true;
      handleShowNextButton();
    });
  }

  return (
    <div className="">
      <h2>{question}</h2>
      <div className="options">
        {options.map((option, index) => {
          return (
            <button
              key={option}
              className={`btn btn-option`}
              ref={(el) => (optionRefs.current[index] = el)}
              onClick={() => {
                handleButtonOptionClick(index);
              }}
            >
              {option}
            </button>
          );
        })}
      </div>
      {showNextButton && (
        <button className="btn" onClick={showingNextQuestion}>
          Next
        </button>
      )}
    </div>
  );
}
