import React, { useReducer, useEffect } from "react";
import Header from "./Header";
import axios from "axios";
import Loader from "./Loader";
import Error from "./Error";
import StartScreen from "./StartScreen";
import Question from "./Question";

const initialState = {
  questions: [],
  status: "loading",
  currentDisplayingQuestionIndex: 0,
  showNext: false,
};

export default function App() {
  const [state, dispatch] = useReducer(reducer, initialState);
  const { questions, status, currentDisplayingQuestionIndex, showNext } = state;

  function reducer(state, action) {
    switch (action.type) {
      case "LOADING":
        return { ...state, status: "loading" };
      case "ERROR":
        return { ...state, status: "error" };
      case "DATA_RECEVIED":
        return { ...state, status: "ready", questions: action.payload };
      case "ACTIVE":
        return { ...state, status: "active" };
      case "SHOW_NEXT":
        return { ...state, showNext: true };
      default:
        throw new Error("action unknown");
    }
  }

  async function fetchQuestions() {
    try {
      dispatch({ type: "LOADING" });
      const { data } = await axios.get("http://localhost:9000/questions");
      dispatch({ type: "DATA_RECEVIED", payload: data });
    } catch (error) {
      dispatch({ type: "ERROR", payload: error });
    }
  }

  useEffect(() => {
    fetchQuestions();
  }, []);

  function handle_LetsStart_ButtonClick() {
    dispatch({ type: "ACTIVE" });
  }

  function handleShowNext() {
    dispatch({ type: "SHOW_NEXT" });
  }

  return (
    <div className="app">
      <Header />
      {status === "loading" && <Loader />}
      {status === "error" && <Error />}
      {status === "ready" && (
        <StartScreen
          numOfQuestions={questions.length}
          handle_LetsStart_ButtonClick={handle_LetsStart_ButtonClick}
        />
      )}
      {status === "active" && (
        <Question
          currentDisplayingQuestion={questions[currentDisplayingQuestionIndex]}
          showNext={showNext}
          handleShowNext={handleShowNext}
        />
      )}
    </div>
  );
}
