import React from "react";

export default function Question({ currentDisplayingQuestion }) {
  console.log(currentDisplayingQuestion);
  const { question, options, correctOption, points } =
    currentDisplayingQuestion;
  return (
    <div className="">
      <h2>{question}</h2>
      <div className="options">
        {options.map((option, index) => {
          return (
            <button
              key={option}
              className={`btn btn-option ${
                index === correctOption ? "correct" : "wrong"
              }`}
            >
              {option}
            </button>
          );
        })}
      </div>
    </div>
  );
}
